import React from "react";
import { Truck } from "lucide-react";

const Layout = ({ children }) => {
  return (
    <div className="min-h-screen bg-yellow-300 flex flex-col">
      {/* Cabeçalho fixo */}
      <header className="fixed top-0 left-0 w-full bg-white shadow-md z-50 py-4 bg-slate-600 flex justify-center">
        <h1 className="text-3xl font-bold text-gray-900 flex items-center justify-center gap-2 bg-green-700">
          <Truck className="h-8 w-8" />
          Sistema de Agendamento de Descarga
        </h1>
      </header>

      {/* Conteúdo principal */}
      <main className="w-full mx-auto ">
        <div className="max-w-7xl mx-auto space-y-6 pb-8">{children}</div>
      </main>

      {/* Rodapé (opcional) */}
      <footer className="bg-gray-800 text-white text-center p-4">
        <p>Rodapé</p>
      </footer>
    </div>
  );
};

export default Layout;
